__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night manual-on sync to Group",
    "parent": 0,
    "comment": "Mirror manual ON of either unit to the group during overnight hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY017_001_1", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY017_002_1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY017_1", "command": "DON", "parameters": [] }
    ],
    "else": [
      { "device": "ZY017_1", "command": "DOF", "parameters": [] }
    ],
    "reasoning": "If either left or right unit is manually turned on (COC) during the overnight window (22:00-06:00 spanning midnight), enable the group so it follows; otherwise ensure the group is off. Parent is root. Uses COC triggers for individual units and a schedule window."
  }
}
__END_NUCORE_ROUTINE__
