__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 201,
    "name": "Night double-tap on F turns off G and H",
    "parent": 0,
    "comment": "If F receives a fast-on control during overnight hours, turn off G and H.",
    "if": [
      { "==": { "device": "71 5F 2E 6", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "71 5F 2E 7", "command": "DOF", "parameters": [] },
      { "device": "71 5F 2E 8", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) on device F combined with an overnight schedule window to avoid accidental day-time triggers; then issue Off (DOF) commands to G and H. No parameters required for DOF."
  }
}
__END_NUCORE_ROUTINE__
