__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query Kasa and Front Door at sunset open",
    "parent": 0,
    "comment": "When front door ST changes to On at sunset, poll Kasa and the sensor",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "37 DE 99 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "n004_tplkasactl", "command": "QUERY", "parameters": [] },
      { "device": "37 DE 99 1", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger when the Front Door Sensor status (ST, uom 51) is On (100) at sunset; then poll the Kasa controller and the sensor to capture current states. Uses explicit sunset schedule and exact status match."
  }
}
__END_NUCORE_ROUTINE__
