__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "F6 pressed -> forward to F7 during weekdays else F8",
    "parent": 0,
    "comment": "When F6 is pressed during weekday work hours send Pressed to F7; otherwise send Pressed to F8",
    "if": [
      { "==": { "device": "n009_irbutton_13f", "control": "GV1" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "n009_irbutton_140", "command": "GV1", "parameters": [] }
    ],
    "else": [
      { "device": "n009_irbutton_141", "command": "GV1", "parameters": [] }
    ],
    "reasoning": "Use a COC trigger for F6 Pressed (GV1) and restrict by a weekly schedule for weekday work hours. If true, emit F7 Pressed; otherwise emit F8 Pressed."
  }
}
__END_NUCORE_ROUTINE__
