__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening double-tap: island full + hall half",
    "parent": 0,
    "comment": "Use DFON on island between 20:00-23:00 to quickly set island to 100% and kitchen hall to 50%",
    "if": [
      { "==": { "device": "55 CB 20 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "20:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "55 CB 20 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "55 CD C5 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Respond to a user-initiated change of control (fast on DFON) on the island. Restrict to an evening window; then set the island to full and kitchen hall to a comfortable 50% for immediate lighting.">
  }
}
__END_NUCORE_ROUTINE__
