__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday morning Heat Ctl ON triggers Rachio query",
    "parent": 0,
    "comment": "When Heat Ctl is physically turned on during weekday mornings, query Rachio",
    "if": [
      { "weekly": { "days":"mon,tue,wed,thu,fri","from":{ "time":"06:00","to":{ "time":"09:00" } } } },
      { "and": 1 },
      { "==": { "device":"25 18 A2 3","control":"DON" } }
    ],
    "then": [
      { "device":"n003_controller","command":"QUERY","parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger for the Heat Ctl DON control to capture the physical ON action; restrict to weekdays 06:00-09:00 using weekly schedule. The Rachio Bridge (n003_controller) is the only device that accepts commands, so call QUERY as the action."
  }
}
__END_NUCORE_ROUTINE__
