__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning curtain press opens both shades and curtains",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device":"n001_3cbbd5f4","control":"DON" } },
      { "and": 1 },
      { "weekly": { "days":"mon,tue,wed,thu,fri","from":{ "time":"07:00","to":{ "time":"09:00" } } } }
    ],
    "then": [
      { "device":"n001_70d30284","command":"DON" },
      { "device":"n001_41d533b5d76","command":"DON" },
      { "device":"n001_3cbbd5f4","command":"DON" }
    ],
    "else": [
      { "device":"n001_3cbbd5f4","command":"DON" }
    ],
    "reasoning": "COC: when Curtains' open control (DON) is activated. During weekdays 07:00-09:00 open both shades and curtains to maximize morning light; outside that window only open Curtains to avoid moving shades at other times."
  }
}
__END_NUCORE_ROUTINE__
