__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Open living room shades on Nook Fast On weekdays",
    "parent": 0,
    "comment": "Open both shades when Nook keypad receives Fast On (COC) during weekday daytime",
    "if": [
      { "==": { "device": "4D 33 4D 3", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "n001_7a71a875acb", "command": "DON", "parameters": [] },
      { "device": "n001_552c880e", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC (keypad DFON) combined with a weekday schedule to open both shades for daytime light when the user actively activates the keypad."
  }
}
__END_NUCORE_ROUTINE__
