__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Mirror BedsideL DFON to BedsideR and FanMed backlight",
    "parent": 0,
    "comment": "When BedsideL is fast-on (COC DFON) set BedsideR and FanMed backlight to index 17 and persist",
    "if": [
      { "==": { "device": "70 12 D0 4", "control": "DFON" } }
    ],
    "then": [
      { "device": "70 12 D0 6", "command": "BL", "parameters": [ { "name": "n/a", "value": 17, "uom": "25", "precision": 0 } ] },
      { "device": "70 12 D0 6", "command": "WDU", "parameters": [] },
      { "device": "70 12 D0 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 17, "uom": "25", "precision": 0 } ] },
      { "device": "70 12 D0 5", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC DFON from BedsideL as a human control trigger to mirror a gentle backlight (index 17) to BedsideR and FanMed and call WDU to persist the change. All actions use accepted commands (BL and WDU) defined on each device."
  }
}
__END_NUCORE_ROUTINE__
