__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion -> fast on rear entry",
    "parent": 0,
    "comment": "Turn ENT1 fast on when either motion sensor detects motion overnight",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY083_155", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY083_380", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "48 1D 4B 1", "command": "DFON" }
    ],
    "else": [],
    "reasoning": "Use motion sensor 'On' send-events (DON) as triggers; restrict to overnight window 22:00-06:00 to avoid daytime activation; DFON (Fast On) ensures immediate illumination."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Both sensors off -> turn rear entry off",
    "parent": 0,
    "comment": "When both sensors send Off events, switch ENT1 off",
    "if": [
      { "==": { "device": "ZY083_155", "control": "DOF" } },
      { "and": 1 },
      { "==": { "device": "ZY083_380", "control": "DOF" } }
    ],
    "then": [
      { "device": "48 1D 4B 1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "When both motion sensors report Off (DOF), assume area is vacant and turn the entry switch off to save energy."
  }
}
__END_NUCORE_ROUTINE__
