__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evening occupancy -> Heat 70F in Dining",
    "parent": 0,
    "comment": "When dining occupancy sensor reports Yes on weekdays 17:00-22:00 set Heat 70F, else Auto",
    "if": [
      { "(": 1 },
      { "==": { "device": "n001_s416400127894", "status": "GV1", "value": 1 } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "17:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      { "device": "n001_t416496082899", "command": "CLIMD", "parameters": [ { "name": "n/a", "value": 1, "uom": "67", "precision": 0 } ] },
      { "device": "n001_t416496082899", "command": "CLISPH", "parameters": [ { "name": "n/a", "value": 700, "uom": "17", "precision": 1 } ] }
    ],
    "else": [
      { "device": "n001_t416496082899", "command": "CLIMD", "parameters": [ { "name": "n/a", "value": 3, "uom": "67", "precision": 0 } ] }
    ],
    "reasoning": "Trigger on occupancy COS (GV1==1) during weekday evening window; set Mode to Heat (1) and Heat Setpoint to 70.0F (value 700 precision 1). Else return Mode to Auto (3)."
  }
}
__END_NUCORE_ROUTINE__
