__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Backyard to 70% if responding",
    "parent": 0,
    "comment": "Set backyard backlight at sunset only when device reports Responding=True",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "9 4F 19 5", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "9 4F 19 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "At sunset, set ControLinc5 Backyard Lights backlight to 70% only if its Responding property equals True (enum value 0). Uses BL with percent UOM 51."
  }
}
__END_NUCORE_ROUTINE__
