__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night DFON alert for Sliding Glass Door.3",
    "parent": 0,
    "comment": "When door 3 DFON overnight, query door 4 and write changes to BU H2O",
    "if": [
      { "==": { "device": "32 AF 85 3", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "32 AF 85 4", "command": "QUERY", "parameters": [] },
      { "device": "53 16 4 1", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) from Sliding Glass Door.3 and restrict to the overnight window (22:00-06:00). Querying Sliding Glass Door.4 obtains its current state; WDU is used to invoke a write action on BU H2O (no writable parameters are defined in the device structure)."
  }
}
__END_NUCORE_ROUTINE__
