__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight Activity: disable motorcycle charger when porch or hall turned on",
    "parent": 0,
    "comment": "Respond to manual (COC) turn-on of Gazebo or Upstairs Hall during overnight window",
    "if": [
      { "(": 1 },
        { "==": { "device": "24 CC 61 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "24 CC 61 1", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "1F BB BC 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "1F BB BC 1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      {
        "device": "3E 4E C3 1",
        "command": "RR",
        "parameters": [
          { "name": "n/a", "value": 12, "uom": "25", "precision": 0 }
        ]
      },
      {
        "device": "3E 4E C3 1",
        "command": "OL",
        "parameters": [
          { "name": "n/a", "value": 0, "uom": "51", "precision": 2 }
        ]
      },
      {
        "device": "3E 4E C3 1",
        "command": "DOF",
        "parameters": []
      }
    ],
    "else": [],
    "reasoning": "If either Gazebo or Upstairs Hall is physically turned on (COC via DON or DFON) during the overnight window (22:00-06:00), immediately make the Motorcycle Charger safe: set a fast ramp rate (1.0 minute = enum 12), set its on level to 0%, then issue an Off command. Actions are ordered so the charger parameters are set before the Off to ensure graceful state transition."
  }
}
__END_NUCORE_ROUTINE__
