__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night On triggers query of other sensors",
    "parent": 0,
    "comment": "When ZY028_252N004 is turned on between 22:00 and 06:00, query ZY028_252N005 and ZY028_252N006",
    "if": [
      { "==": { "device": "ZY028_252N004", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY028_252N005", "command": "QUERY", "parameters": [] },
      { "device": "ZY028_252N006", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the specified device is manually turned on (a control event) during overnight hours, immediately poll the two associated sensors to capture their current state following that control event."
  }
}
__END_NUCORE_ROUTINE__
