__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday DFON sets patio fan light 50% and motor Low",
    "parent": 0,
    "comment": "Activate fan when chandelier Fast On during weekday evenings",
    "if": [
      { "==": { "device": "ZY015_1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "70 91 AC 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 2 } ] },
      { "device": "70 91 AC 2", "command": "DON", "parameters": [ { "name": "n/a", "value": 25, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A DFON (user control) on the Dining Room Chandelier during weekday evenings should start the patio lighting at 50% and set the motor to Low (25). Using a COC (DFON) ensures user intent; weekly time restricts to mon-fri 18:00-23:00."
  }
}
__END_NUCORE_ROUTINE__
