__BEGIN_NUCORE_ROUTINE__
{ 
  "routine": {
    "id": 1,
    "name": "Weekend manual laser-on activates group",
    "parent": 0,
    "comment": "Enable group when either sub-device is manually turned on during weekend evening window; otherwise disable group.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY011_001_1", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY011_002_1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "sat,sun", "from": { "time": "20:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "ZY011_1", "command": "DON", "parameters": [] }
    ],
    "else": [
      { "device": "ZY011_1", "command": "DOF", "parameters": [] }
    ],
    "reasoning": "Use COC (manual DON) from either sub-device combined with a weekend evening schedule to decide group power. If triggered, send group DON; otherwise ensure group is DOF."
  }
}
__END_NUCORE_ROUTINE__
