__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Auto-lock front door after sunset on manual unlock",
    "parent": 0,
    "comment": "Lock zFrontDoor when it is unlocked after sunset",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY005_1", "status": "ST", "value": 0, "uom": "11", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY005_1", "command": "LOCK", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger when local sunset occurs and the zFrontDoor status becomes Unlocked (ST = 0). Then send the LOCK command to the same device to secure the door. Using the door's ST enum and uom id 11 avoids referencing other devices."
  }
}
__END_NUCORE_ROUTINE__
