__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Entryway on at sunset if hallway off",
    "parent": 0,
    "comment": "Turn on EntryWay if Hallway is off at sunset+10m, else set kitchen to warm white",
    "if": [
      { "at": { "sunset": 600 } },
      { "and": 1 },
      { "==": { "device": "33 86 FE 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "2A 5D 32 1", "command": "DON", "parameters": [] }
    ],
    "else": [
      { "device": "n002_d073d57536b1", "command": "CLITEMP", "parameters": [ { "name": "n/a", "value": 3500, "uom": "26", "precision": 1 } ] }
    ],
    "reasoning": "Use sunset+10min as schedule. Check Hallway ST equals Off (0). If true, turn on EntryWayIntLight (DON). If not, set LIFX Kitchen color temperature to 3500K using CLITEMP parameter."
  }
}
__END_NUCORE_ROUTINE__
