__BEGIN_NUCORE_ROUTINE__
{ 
  "routine": {
    "id": 1,
    "name": "Turn on kitchen lights at sunset if off",
    "parent": 0,
    "comment": "Enable all kitchen lights at sunset only when each is off",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "2A 3B 44 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "==": { "device": "2A 54 E5 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "==": { "device": "2A 3C 4E 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "2A 3B 44 1", "command": "DON" },
      { "device": "2A 54 E5 1", "command": "DON" },
      { "device": "2A 3C 4E 1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Scheduled at sunset. The routine checks each light's Status (ST) equals Off (0) before sending DON so it only issues commands when necessary."
  }
}
__END_NUCORE_ROUTINE__
