__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Kitchen Dimmer FastOn Weekday Set 75%",
    "parent": 0,
    "comment": "When user performs Fast On (COC) on Kitchen Dimmer during weekday business hours, set level to 75%.",
    "if": [
      { "==": { "device": "ZY003_1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY003_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 75, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "COC trigger uses the DFON control from Kitchen Dimmer; restrict to weekdays 09:00-17:00 and issue DON with 75% (uom 51) to set level when Fast On is pressed."
  }
}
__END_NUCORE_ROUTINE__
