__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night All Off when SwitchAll set to Ignore None",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device":"ZY002_183","status":"ST","value":255 } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"ZY002_183","command":"ALLOFF","parameters": [] }
    ],
    "else": [],
    "reasoning": "When the Switch All status changes to Ignore None (255) during night hours, turn all off to ensure devices aren't left on."
  }
}
__END_NUCORE_ROUTINE__
