__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query closed window and beep middle on fast-open during weekdays",
    "parent": 0,
    "comment": "Trigger on COC DFON during weekday daytime",
    "if": [
      { "(": 1 },
        { "==": { "device": "37 3 44 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "37 3 44 2", "command": "QUERY" },
      { "device": "37 5 54 4", "command": "BEEP" }
    ],
    "else": [],
    "reasoning": "Run when the opened window receives a fast-on control during weekday daytime; query the closed window state and beep the heartbeat device to alert. Uses COC on the opened window and a weekly schedule to limit to business hours."
  }
}
__END_NUCORE_ROUTINE__
