__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night open alert for sliding door",
    "parent": 0,
    "comment": "Beep weight room window and query closed sensor when sliding door opens at night",
    "if": [
      { "==": { "device": "4E 3A 92 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "37 3 44 4", "command": "BEEP", "parameters": [] },
      { "device": "4E 3A 92 2", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the Sliding Glass Door reports ST=On (100) during the overnight window (22:00->06:00), notify local window device via BEEP and poll the closed-door sensor with QUERY to confirm state. Uses only accepts commands defined on devices."
  }
}
__END_NUCORE_ROUTINE__
