__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night leak alert beeps",
    "parent": 0,
    "comment": "Beep on leak detected overnight",
    "if": [
      { "==": { "device": "36 A9 CB 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "36 A9 CB 1", "command": "BEEP" },
      { "device": "21 D6 A1 1", "command": "BEEP" }
    ],
    "else": [],
    "reasoning": "When the leak sensor status becomes On during the night period (22:00-06:00), emit audible alerts on the leak sensor and the living room motion sensor to ensure immediate awareness." 
  }
}
__END_NUCORE_ROUTINE__
