  "routine": {
    "id": 1,
    "name": "Lock Front Door at sunset if battery sufficient",
    "parent": 0,
    "comment": "Lock Front Door OLD at sunset only when battery >= 10%",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { ">=": { "device": "n010_28259168", "status": "GV1", "value": 10, "uom": "51", "precision": 1 } }
    ],
    "then": [
      { "device": "n010_28259168", "command": "LOCK", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Run at sunset and only lock if the door battery is at least 10% to avoid lock operation failures."
  }
}
__END_NUCORE_ROUTINE__
