__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night auto-lock when door closed and unlocked",
    "parent": 0,
    "comment": "Lock door if it becomes closed while unlocked between 22:00 and 06:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "n010_78173245", "status": "GV4", "value": 100, "uom": "79" } },
        { "and": 1 },
        { "==": { "device": "n010_78173245", "status": "GV2", "value": 0, "uom": "11" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n010_78173245", "command": "LOCK", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Ensure the door is secured overnight: when the door reports Closed and the lock is Unlocked between 22:00 and 06:00, send the Lock command."
  }
}
__END_NUCORE_ROUTINE__
