__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset patio on when bulbs present, else notify",
    "parent": 0,
    "comment": "Turn on Patio Lights at sunset when LiFX has bulbs; otherwise notify",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { ">=": { "device": "n002_lifxctl", "status": "GV0", "value": 1, "uom": "56", "precision": 1 } }
    ],
    "then": [
      { "device": "4A AC 3F 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 1 } ] }
    ],
    "else": [
      { "device": "n001_controller", "command": "SET_MESSAGE", "parameters": [ { "name": "n/a", "value": 0, "uom": "25", "precision": 0 } ] }
    ],
    "reasoning": "At sunset, check the LiFX Controller's Bulbs discovered (GV0). If one or more bulbs are discovered (GV0 >= 1), turn Patio Lights (4A AC 3F 1) on to 60% using DON with a % parameter. If no bulbs are discovered, send a message via the Notification Controller using SET_MESSAGE."
  }
}
__END_NUCORE_ROUTINE__
