__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Weekday morning: IR Button R triggers IR Button S",
    "parent": 0,
    "comment": "When R is pressed between 06:00-09:00 on weekdays, simulate S Pressed",
    "if": [
      { "==": { "device": "n009_irbutton_115", "control": "Pressed" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "n009_irbutton_116", "command": "Pressed" }
    ],
    "else": [],
    "reasoning": "Use a COC "when pressed" trigger on IR Button R and restrict to weekday mornings with a weekly schedule. The action invokes the Pressed send-command on IR Button S to chain IR behavior." 
  }
}
__END_NUCORE_ROUTINE__
