__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning manual Zone 8 start - pause handling",
    "parent": 0,
    "comment": "If Zone 8 is physically started during weekday morning window and controller is paused, stop controller; otherwise enforce 20 minute max start",
    "if": [
      { "(": 1 },
        { "==": { "device": "n011_449160fc17908", "control": "START", "parameters": [ { "name": "n/a", "value": 30, "uom": "45", "precision": 1 } ] } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } },
      { ")": 1 },
      { "and": 1 },
      { "==": { "device": "n011_449160fc1790", "status": "GV2", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n011_449160fc1790", "command": "STOP", "parameters": [] }
    ],
    "else": [
      { "device": "n011_449160fc17908", "command": "START", "parameters": [ { "name": "n/a", "value": 20, "uom": "45", "precision": 1 } ] }
    ],
    "reasoning": "COC on Zone 8 during weekday morning is handled first; if controller is paused we stop it to avoid overlapping or unmanaged runs. If not paused we enforce a 20 minute cap by issuing a START with 20 minutes. START param uses UOM 45 and precision 1 as defined."
  }
}
__END_NUCORE_ROUTINE__
