__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Notify pump when Stephbath WET becomes On",
    "parent": 0,
    "comment": "On COS of wet sensor -> alert and write changes to kitchen pump",
    "if": [
      { "==": { "device": "2D 62 1E 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "21 7E 5F 4", "command": "BEEP", "parameters": [] },
      { "device": "21 7E 5F 4", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "The trigger is a COS: the wet sensor ST becomes On (value 100). The pump accepts BEEP and WDU; so on that COS the routine alerts the pump and issues a write-changes command to prompt any required action."
  }
}
__END_NUCORE_ROUTINE__
