__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Double-tap billiard1 sets billiard2 with night dim",
    "parent": 0,
    "comment": "Double-tap (fast on) on device 1 triggers different levels depending on night window",
    "if": [
      { "==": { "device": "1D CF BC 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "1D C7 9A 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "1D C7 9A 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "1D C7 9A 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 3, "uom": "25", "precision": 0 } ] },
      { "device": "1D C7 9A 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "reasoning": "Trigger on DFON (COC) from billiard 1; during nightly hours (22:00–06:00) apply gentle 1.0 min ramp to 30% on billiard 2, otherwise apply faster 6.0 min ramp to 80%. Uses RR then OL/DON to set ramp and level."
  }
}
__END_NUCORE_ROUTINE__
