__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night open alert - DoorSensor.3",
    "parent": 0,
    "comment": "Beep parent when DoorSensor.3 COS to On during overnight hours",
    "if": [
      { "==": { "device": "2C 6F 36 3", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "2C 6F 36 1", "command": "BEEP", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COS on child device 2C 6F 36 3 (ST == On) combined with an overnight schedule to only alert at night; the parent device (2C 6F 36 1) supports BEEP so use it as the alert action."
  }
}
__END_NUCORE_ROUTINE__
