__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Sunset On -> Identify Main, else Turn Light Off",
    "parent": 0,
    "comment": "If Switch.Light becomes ON at sunset, identify the main device for 5s; else turn the light off.",
    "if": [
      { "==": { "device": "20 6C 3C 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "ZB47472_001_1", "command": "IDENTIFY", "parameters": [ { "name": "n/a", "value": 5, "uom": "58", "precision": 0 } ] }
    ],
    "else": [
      { "device": "20 6C 3C 1", "command": "DOF" }
    ],
    "reasoning": "Trigger uses a COS check (status == On) combined with the sunset schedule to act only when the light turns on at sunset. The 'then' calls IDENTIFY on the Main device for 5 seconds using the defined seconds UOM 58. The else safely turns the light off using DOF."
  }
}
__END_NUCORE_ROUTINE__
