__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: turn on Ceiling Fan Light",
    "parent": 0,
    "comment": "Turn on fan light at sunset if it's off",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n001_914c2f52ff1_dl", "status": "ST", "value": 0, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "n001_914c2f52ff1_dl", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "At sunset, if the Ceiling Fan Light is off (ST=0) turn it on to provide evening lighting."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Sunset: boost dimmer and set fan direction if dimmer low",
    "parent": 0,
    "comment": "If dimmer is below 20% at sunset, set to 50% and set fan direction to Forward",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "<": { "device": "4F 3A 6C 1", "status": "ST", "value": 20, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "4F 3A 6C 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] },
      { "device": "n001_914c2f52ff1", "command": "SET_DIRECTION", "parameters": [ { "name": "n/a", "value": 1, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When ambient lighting is low (dimmer <20%) at sunset, raise the dimmer to 50% and set the fan direction to Forward for comfort."
  }
}
__END_NUCORE_ROUTINE__
