__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening double-tap: living room 70% / kitchen off",
    "parent": 0,
    "comment": "Double-tap (fast on) in foyer during weekday evenings",
    "if": [
      { "(": 1 },
        { "==": { "device": "1A 57 EF 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "17:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      { "device": "56 82 72 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] },
      { "device": "45 30 8B 1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use the foyer DFON send as a change-of-control trigger; restrict to weekdays 17:00-22:00. When triggered set Living Room Ceiling to 70% (DON with uom 51) and turn Kitchen Ceiling off (DOF)."
  }
}
__END_NUCORE_ROUTINE__
