__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn on Christmas lights when front wall switched on after sunset",
    "parent": 0,
    "comment": "If LR4-FRONT WALL LTS (ZY003_1) is turned on manually after sunset then turn on SPL1-CHRISTMAS (ZY004_1)",
    "if": [
      { "(": 1 },
      { "==": { "device": "ZY003_1", "control": "DON" } },
      { "and": 1 },
      { "at": { "sunset": 0 } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZY004_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger on ZY003_1 (manual ON) combined with sunset to limit action to evening. When the front wall light is manually turned on after sunset the user likely wants exterior/holiday lighting; turn on the Christmas lights (ZY004_1) using its DON command."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Turn off Christmas lights when front wall switched off",
    "parent": 0,
    "comment": "When LR4-FRONT WALL LTS (ZY003_1) is turned off manually, turn off SPL1-CHRISTMAS (ZY004_1)",
    "if": [
      { "==": { "device": "ZY003_1", "control": "DOF" } }
    ],
    "then": [
      { "device": "ZY004_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Mirror the manual OFF (COC) from the front wall light to the Christmas lights so manual control is consistent across both devices."
  }
}
__END_NUCORE_ROUTINE__
