__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn basement off when garage TV turns on after sunset",
    "parent": 0,
    "comment": "If GAR6-TV_RESET-ZM_WW becomes On at sunset, switch BSMT6-XAPNET_RESET-ZM_WW Off",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "ZM00008_001_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "ZM00007_001_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Combine a schedule (sunset) with a COS on the garage TV (status -> On) to ensure the basement reset is turned off at night for safety."
  }
}
__END_NUCORE_ROUTINE__
