__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday Night: Set Dining Ecobee to Away and Hold",
    "parent": 0,
    "comment": "Sets mode to Away at 22:00 Mon-Fri, fan Auto, and hold schedule indefinitely if controller connected",
    "if": [
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "22:00", "to": { "time": "22:01" } } } },
      { "and": 1 },
      { "==": { "device": "n001_controller", "status": "GV1", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n001_t417350996561", "command": "CLIMD", "parameters": [ { "name": "n/a", "value": 13, "uom": "67", "precision": 0 } ] },
      { "device": "n001_t417350996561", "command": "CLIFS", "parameters": [ { "name": "n/a", "value": 0, "uom": "68", "precision": 0 } ] },
      { "device": "n001_t417350996561", "command": "CLISMD", "parameters": [ { "name": "n/a", "value": 2, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Run only on weekdays at 22:00 and only when the Ecobee controller reports connection (GV1==True). Then set Mode to Away (enum value 13), Fan Mode to Auto (0), and Place Schedule on Hold Indefinite (CLISMD value 2). The short to-window (22:00-22:01) ensures a scheduled trigger at that exact time range." 
  }
}
__END_NUCORE_ROUTINE__
