__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night manual trigger - enable exterior relays",
    "parent": 0,
    "comment": "When either Front Porch or Turret receives a manual Fast On (DFON) between 22:00-06:00, turn on Turret and Eaves for safety lighting.",
    "if": [
      { "(": 1 },
        { "==": { "device": "21 F 97 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "21 B 80 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "21 B 80 1", "command": "DON" },
      { "device": "21 D 9D 1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Use COC triggers (DFON) on Front Porch or Turret during night hours to immediately enable exterior lighting (Turret and Eaves). Restrict to 22:00-06:00 to avoid daytime activations."
  }
}
__END_NUCORE_ROUTINE__
