__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: set bm firepl sitt to 60%",
    "parent": 0,
    "comment": "Turn device on to 60% at sunset daily",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device":"1D C0 A7 1","command":"OL","parameters":[{ "name":"n/a","value":60,"uom":"51","precision":2 }] }
    ],
    "else": [],
    "reasoning": "Schedule trigger at sunset sets On Level to 60% using the device's OL command and uom 51."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Manual DFON sets ramp to 1.0 minute for bm firepl sitt",
    "parent": 0,
    "comment": "When the device receives a DFON (manual fast on), set ramp rate to enum value 12 (1.0 minute)",
    "if": [
      { "==": { "device":"1D C0 A7 1","control":"DFON" } }
    ],
    "then": [
      { "device":"1D C0 A7 1","command":"RR","parameters":[{ "name":"n/a","value":12,"uom":"25","precision":0 }] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) to trigger setting Ramp Rate to the enum index for 1.0 minute (value 12) so manual activations use a smooth 1-minute ramp."
  }
}
__END_NUCORE_ROUTINE__
