__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Double-tap morning: set warm color and on-level",
    "parent": 0,
    "comment": "Double-tap between 07:00-09:00 weekdays",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZB07489_011_1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "ZB07489_011_106", "command": "MOVETO", "parameters": [ { "name": "HUE", "value": 30, "uom": "149", "precision": 0 }, { "name": "SAT", "value": 200, "precision": 0 } ] },
      { "device": "ZB07489_011_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 200, "uom": "100", "precision": 0 }, { "name": "Ramp Rate", "value": 5, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on a Fast On (COC) only during weekday morning window; first set Hue/Saturation on the Hue-Sat endpoint, then send an On command to the main switch with the requested level and ramp rate. No else actions required."
  }
}
__END_NUCORE_ROUTINE__
