__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Close shades overnight when freezing",
    "parent": 0,
    "comment": "Close Den and Living Room shades from 22:00 to 06:00 if NOAA temperature <= 32°F",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "<=": { "device":"n001_controller","status":"CLITEMP","value":32,"uom":"17","precision":1 } }
    ],
    "then": [
      { "device":"ZY015_1","command":"DOF" },
      { "device":"ZY014_1","command":"DOF" }
    ],
    "else": [],
    "reasoning": "When nighttime hours (22:00–06:00) coincide with outdoor temps at or below freezing (<=32°F, CLITEMP with uom 17, precision 1), close both window shades to reduce heat loss. This routine triggers at the COS moment the condition becomes true during the overnight window."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Open shades at sunrise",
    "parent": 0,
    "comment": "Open Den and Living Room shades at sunrise daily",
    "if": [
      { "at": { "sunrise": 0 } }
    ],
    "then": [
      { "device":"ZY015_1","command":"DON" },
      { "device":"ZY014_1","command":"DON" }
    ],
    "else": [],
    "reasoning": "At sunrise (sunrise:0) open both shades to allow natural light. Separate routine keeps sunrise action independent of temperature checks."
  }
}
__END_NUCORE_ROUTINE__
