__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night double-tap sets SALA C and MASTER LED",
    "parent": 0,
    "comment": "Trigger on SALA D fast-on during night hours",
    "if": [
      { "==": { "device":"32 99 BD 1","control":"DFON" } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"32 97 8A 1","command":"RR","parameters":[ { "name":"n/a","value":12,"uom":"25","precision":0 } ] },
      { "device":"32 97 8A 1","command":"OL","parameters":[ { "name":"n/a","value":80,"uom":"51","precision":0 } ] },
      { "device":"2B 14 BD 1","command":"BL","parameters":[ { "name":"n/a","value":0,"uom":"51","precision":0 } ] }
    ],
    "else": [],
    "reasoning": "Use DFON COC on SALA D combined with an overnight schedule. Set SALA C's ramp rate to the 1.0 minute enum (value 12, uom 25) then set its on-level to 80% (uom 51). Turn MASTER LED backlight to 0% to keep it off at night."
  }
}
__END_NUCORE_ROUTINE__
