__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Beep door when dusk motion after sunset",
    "parent": 0,
    "comment": "When new motion sensor-Dusk.Dawn goes On after sunset, send a beep to Sliding Glass Door.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "21 D6 AB 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "32 AF 85 1", "command": "BEEP", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger when Dusk.Dawn sensor reports On (motion) at or after sunset; use BEEP on the Sliding Glass Door (accepted command) to notify. No parameters defined for BEEP in DEVICE STRUCTURE, so parameters array is empty."
  }
}
__END_NUCORE_ROUTINE__
