__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion turns on bad controlinc backlight",
    "parent": 0,
    "comment": "When Dusk.Dawn sensor changes to On during nighttime, set backlight to 100% else 0%",
    "if": [
      { "==": { "device": "31 AB F6 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "9 4B 9C 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "9 4B 9C 5", "command": "WDU", "parameters": [] }
    ],
    "else": [
      { "device": "9 4B 9C 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 0, "uom": "51", "precision": 0 } ] },
      { "device": "9 4B 9C 5", "command": "WDU", "parameters": [] }
    ],
    "reasoning": "Enable backlight to full when the Dusk.Dawn motion sensor reports On during defined night hours (22:00-06:00). Use WDU to persist the change. If no night motion (else), ensure backlight is off and persist."
  }
}
__END_NUCORE_ROUTINE__
