__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query Party Lights when Master Bath switches used or at sunset",
    "parent": 0,
    "comment": "Query Party Lights (ZY020_252N002) when either Master Bath switch is physically turned on or at sunset.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY008_252N002", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY008_252N003", "control": "DON" } },
      { ")": 1 },
      { "or": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "ZY020_252N002", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "User wants Party Lights queried when either Master Bath control is physically turned on (COC) or at sunset. Devices only accept QUERY, so routine issues a QUERY to ZY020_252N002.">
  }
}
__END_NUCORE_ROUTINE__
