__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query peers when ZY028_252N004 turns On",
    "parent": 0,
    "comment": "When device 004 emits On, poll the other two devices immediately",
    "if": [
      { "==": { "device": "ZY028_252N004", "control": "DON" } }
    ],
    "then": [
      { "device": "ZY028_252N005", "command": "QUERY", "parameters": [] },
      { "device": "ZY028_252N006", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "A COC event from ZY028_252N004 (On) indicates an alarm or trigger; immediate queries to 005 and 006 gather their current states for correlation. Uses only accepted 'QUERY' command on each device."
  }
}
__END_NUCORE_ROUTINE__
