__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening: chandelier fast-on -> patio light 50% and fan Low",
    "parent": 0,
    "comment": "Triggered by a physical fast-on of the chandelier during evening hours",
    "if": [
      { "from": { "time": "19:00", "to": { "time": "23:00", "day": 0 } } },
      { "and": 1 },
      { "==": { "device": "ZY015_1", "control": "DFON" } }
    ],
    "then": [
      { "device": "70 91 AC 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] },
      { "device": "70 91 AC 2", "command": "DON", "parameters": [ { "name": "n/a", "value": 25, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A COC (DFON) on the Dining Room Chandelier during evening hours should immediately light the patio to a comfortable 50% and set the fan motor to Low for outdoor comfort."
  }
}
__END_NUCORE_ROUTINE__
