__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday: U press triggers W Pressed",
    "parent": 0,
    "comment": "When IR Button U is physically pressed during weekday work hours, trigger W Pressed",
    "if": [
      { "==": { "device": "n009_irbutton_118", "control": "GV1" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "n009_irbutton_11a", "command": "Pressed", "parameters": [] }
    ],
    "else": [],
    "reasoning": "COC on n009_irbutton_118 (Pressed = GV1) during weekday hours replicates the press to n009_irbutton_11a by sending its Pressed send-command. Weekly schedule restricts to mon-fri 09:00-17:00."
  }
}
__END_NUCORE_ROUTINE__
